<?phpclass Prototype{    public $name='bb';    public function __construct($name=''){        $this->name = $name;    }    public function getName(){        echo  $this->name;    }    public function setName($value){        $this->name = $value;    }    public function getPrototype(){        return clone $this;    }}$prototype = new Prototype('aa');$prototype->getName();$prototype2 = $prototype->getPrototype();$prototype2->getName();